def test_log():
    # 日志同时输出到控制台和logs/info.log
    log_tool.info('测试info日志')
    try:
        excel_tool.get_test_case('')
    except Exception as e:
        # 日志同时输出到控制台和logs/error.log
        log_tool.error('测试error日志')
        log_tool.error(e)

def test_getCustomer(driver_data):
    # config/conf.py里面配置GY_API_URL,模板快捷键demo_conf_api
    url = 'http://dev.guoyasoft.com:8080/cst/getCustomer'
    req = {'phone':driver_data['phone']}
    # req = {'phone':'15113794711'}
    headers = {
            'token':driver_data['token'],
            'charset':'UTF-8'
    }
    resp = request_tool.get_request(url,params=req,headers=headers)
    body = resp.json()
    # 判断响应码
    assert_tool.assert_equal(resp.status_code, 200)
    # 自定义断言
    assert_tool.assert_equal(body['code'], 2000)

