########### 注册接口sign_up.xlsx ################ # case_name i_user_name i_pwd i_repwd i_phone o_code o_userId # 注册成功 自动生成 自动生成 自动生成 自动生成 2000 not null # 用户名已存在 wuling1323 自动生成 自动生成 自动生成 9999 # 手机号已存在 自动生成 自动生成 自动生成 18699872345 9999 ############################################# data = excel_tool.get_test_case('data/user/注册接口sign_up.xlsx')
@pytest.mark.parametrize('i_user_name,i_pwd,i_repwd,i_phone,o_code,o_userId', data[1], ids=data[0]) def test_sign_up_together(i_user_name, i_pwd, i_repwd, i_phone, o_code, o_userId): # config/conf.py里面配置GY_API_URL = 'http://dev.guoyasoft.com:8080' url = conf.GY_API_URL + '/signup' if i_user_name == '自动生成': pinyin=random_tool.random_name_pinyin() num=str(random_tool.random_number(0,9)) i_user_name = pinyin+num if i_pwd == '自动生成': i_pwd=random_tool.random_pwd() if i_repwd == '自动生成': i_repwd=i_pwd if i_phone == '自动生成': i_phone = random_tool.random_tell()
req = { "phone": i_phone, "pwd": i_pwd, "rePwd": i_repwd, "userName": i_user_name } resp = request_tool.post_request(url, json=req) body = resp.json() # 判断响应码 assert_tool.assert_equal(resp.status_code, 200) # 自定义断言 assert_tool.assert_equal(body['code'], o_code) data = body['data'] if o_userId == 'not null' and data != '': user_id = data['userId'] assert_tool.assert_not_null(user_id)
